import { Component, OnInit } from '@angular/core';
import { CategoryService } from '../services/category.service';
import { Category } from 'src/app/models/category.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-category-list',
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.css']
})
export class CategoryListComponent implements OnInit {

  // categories?: Category[];
  //observable
  categories$?: Observable<Category[]>;
  constructor(private categoryService: CategoryService) {

  }

  ngOnInit(): void {
    this.categories$ = this.categoryService.getAllCategories();
    // this.categoryService.getAllCategories()
    //   .subscribe({
    //     next: (response) => {
    //       console.log(response);
    //       this.categories = response;
    //     }
    //   })
  }
}
<div class="container">
    <h1 class="mt-3">Category List</h1>
    <div class="d-flex justify-content-end mt-3">
        <a [routerLink]="['/admin/categories/add']" class="btn btn-primary">Add Category</a>        
    </div>

    <ng-container *ngIf="categories$ | async as categories">
        <table class="table table-bordered mt-3" width=80%>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>URL Handle</th>
                <th></th>
            </tr>
            <tr *ngFor="let category of categories">
                <td>{{category.id}}</td>
                <td>{{category.name}}</td>
                <td>{{category.urlHandle}}</td>
                <td>
                    <a class="btn btn-light" [routerLink]="['admin/categories',category.id]">Edit</a>
                </td>
            </tr>
        </table>
    </ng-container>
</div>
